}
// 给aoma对象设置一个品牌属性
Object.defineProperty(aoma,"brand",{
    // 在这里对brand属性进行描述
    value: "奥玛",
    writable: true,
    configurable:true,
    enumrable: true
})
console.log(aoma.brand); //输出结果 "奥玛"
aoma.brand = "美的";
console.log(aoma.brand);//品牌变成了 "美的"
delete aoma.brand;
consoel.log(aoma.brand);//brand又被删除了,所有输出结果是undefined
//遍历aoma对象里有哪些属性
for(var key in aoma){
    console.log(key);
}

var aoma = {
    size: 60,//尺寸
